windowlist.script:
				local sFocus = "name";

				function onInit()
				if newfocus then
				sFocus = newfocus[1];
				end
				end

				function onListChanged()
				update();
				end

				function update()
				local sEdit = getName() .. "_iedit";
				if window[sEdit] then
				local bEdit = (window[sEdit].getValue() == 1);
				for _,w in ipairs(getWindows()) do
				w.idelete.setVisible(bEdit);
				end
				end
				end

				function onClickDown(button, x, y)
				if not isReadOnly() then
				return true;
				end
				end

				function onClickRelease(button, x, y)
				if not isReadOnly() then
				if getWindowCount() == 0 then
				addEntry(true);
				end
				return true;
				end
				end

				function addEntry(bFocus)
				local w = createWindow();
				if bFocus then
				w[sFocus].setFocus();
				end
				return w;
				end

buttoncontrol.script:
function onClickDown()
return true;
end
function onClickRelease()
local nValue = getValue();
if (nValue == 2) or (nValue == 3) then
window.unshare();
end
return true;
end

stringcontrol.script:
				function onGainFocus()
				setCursorPosition(#getValue()+1);
				setSelectionPosition(1);
				end

				function onLoseFocus()
				setCursorPosition(1);
				setSelectionPosition(1);
				end

				function onValueChanged()
				if isEmpty() then
				setFrame("search", 22,5,5,5);
				else
				setFrame("search_active", 22,5,5,5);
				end
				end

				function onClickDown(button)
				if button == 2 then
				return true;
				end
				end

				function onClickRelease(button)
				if button == 2 then
				setValue("");
				return true;
				end
				end
