list_utility.script:
function onDrop(x, y, draginfo)
if User.isHost() then
if draginfo.getType() == "number" then
local w = addEntry(true);
if w then
w.label.setValue(draginfo.getDescription());
w.bonus.setValue(draginfo.getNumberData());
end
return true;
end
end
end

windowclass.script:
			function onInit()
			if not User.isHost() then
			isgmonly.setVisible(false);
			label.setAnchor("left", "", "left", "absolute", 2);
			end
			end

			function onDragStart(button, x, y, draginfo)
			if not label.isEmpty() then
			draginfo.setType("number");
			draginfo.setDescription(label.getValue());
			draginfo.setStringData(label.getValue());
			draginfo.setNumberData(bonus.getValue());
			end
			return true;
			end

			function onDoubleClick(x, y)
			ModifierStack.addSlot(label.getValue(), bonus.getValue());
			return true;
			end

			function onDrop(x, y, draginfo)
			return windowlist.onDrop(x, y, draginfo);
			end

basicnumber.script:
					function onDragStart(button, x, y, draginfo)
					return window.onDragStart(button, x, y, draginfo);
					end

					function
					onDoubleClick(x, y)
					return window.onDoubleClick(x, y);
					end

					function onGainFocus()
					window.setFrame("rowshade");
					end

					function onLoseFocus()
					window.setFrame(nil);
					end

string_textlistitem.script:
					function onDragStart(button, x, y, draginfo)
					return window.onDragStart(button, x, y, draginfo);
					end

					function
					onDoubleClick(x, y)
					return window.onDoubleClick(x, y);
					end
